var Elements = 
{
    Id:function(id)
    {
       return document.getElementById(id);
    },
    ClassName:function(name)
    {
        return document.getElementsByClassName(name);
    },
    Name:function(name)
    {
        return document.getElementsByName(name);
    },
    TagName:function(name)
    {
        return document.getElementsByTagName(name);
    }
}
