

var Polynomail = 
{
    Variable:function(b,pow)
    {
        return {B:b,Pow:pow}
    },
    New:function()
    {
       return {       
            Equation:[],
            Calculate:function(x)
            {
                var result = this.Equation[0];
                
                for(let i = 1 ;i<this.Equation.length;i++)
                {
                    result += this.Equation[i] * Math.pow(x,i);
                }
                return result ;
            },
            GenerateNewGeneration:function (deviate) 
            {
                var generationList = [];
                var pl = Polynomail.New();
                pl.Equation = this.Equation;
                generationList.push(pl);
                for(let i=0;i<this.Equation.length;i++)
                {
                    var carry = generationList;
                    carry.forEach(element => {
                        var c = element;
                        c.Equation[i] += deviate;
                        generationList.push(element);
                        c = element;
                        c.Equation[i] -= deviate;
                        generationList.push(element);
                    });
                }
                return generationList;
            }
        }
    }
}
