

var Polynomail = 
{
    Variable:function(b,x,pow){return {B:b,Pow:pow}},
    New:function()
    {
       return {       
            Equation:[],
            Calculate:function(x)
            {
                var result = 0;
                this.Equation.forEach(element => {
                   result += element.B * Math.pow(x,element.Pow)  
                });
                return 1 ;
            }
        }
    }
}
